"use strict";!function(){var r={options:{dataUrl:"./common/json/guide.json",tmpl:'<li class="raffle-item hg-flex"><div class="img-box"><img src="{{liv_src}}"/></div><div class="hg-flex-one"><h2 class="raffle-title">{{liv_title}}</h2><p class="raffle-brief">{{liv_brief}}</p></div><a class="outlink" href="{{liv_href}}"></a></li>'},init:function(){r.ajaxInfo()},tips:function(){"Other"===window.utils.mobileDevice()&&($(".qrcode-box").removeClass("hide"),r.qrcode())},qrcode:function(){new QRCode($(".qrcode-area")[0],{text:window.location.href,width:128,height:128,colorDark:"#000",colorLight:"#fff",correctLevel:QRCode.CorrectLevel.H})},ajaxInfo:function(){window.utils.http({url:r.options.dataUrl,success:function(e){e&&e[0]?r.renderView(e):console.warn("引导页信息错误")},error:function(e){console.warn("引导页信息错误")}})},renderView:function(e){var i="";e.forEach(function(o){i+=r.options.tmpl.replace(/{{liv_(.*?)}}/g,function(e,i){return"href"===i?e.replace("{{liv_href}}","./"+o.key):"src"===i?e.replace("{{liv_src}}","./common/images/guide/"+o.key+".png"):e.replace("{{liv_"+i+"}}",o[i]||"")})}),$(".page-guide").append(i)}};r.init()}();