"use strict";!function(){var l={options:{dataUrl:"./common/json/guide.json",tmpl:'<li class="raffle-item hg-flex"><div class="img-box"><img src="{{liv_src}}"/></div><div class="hg-flex-one"><h2 class="raffle-title">{{liv_title}}</h2><p class="raffle-brief">{{liv_brief}}</p></div><a class="outlink" href="{{liv_href}}"></a></li>'},init:function(){l.tips(),l.ajaxInfo()},tips:function(){"Other"===window.utils.mobileDevice()&&$(".tips").removeClass("hide")},ajaxInfo:function(){window.utils.http({url:l.options.dataUrl,success:function(i){i&&i[0]?l.renderView(i):console.warn("引导页信息错误")},error:function(i){console.warn("引导页信息错误")}})},renderView:function(i){var e="";i.forEach(function(n){e+=l.options.tmpl.replace(/{{liv_(.*?)}}/g,function(i,e){return"href"===e?i.replace("{{liv_href}}","./"+n.key):"src"===e?i.replace("{{liv_src}}","./common/images/guide/"+n.key+".png"):i.replace("{{liv_"+e+"}}",n[e]||"")})}),$(".page-guide").append(e)}};l.init()}();